graphql.query:
mutation dropTable {
    users: dropTable(
        keyspaceName:"papertrader"
        tableName:"users"
    )
    games: dropTable(
        keyspaceName:"papertrader"
        tableName:"games"
    )
    portfolios: dropTable(
        keyspaceName:"papertrader"
        tableName:"portfolios"
    )
    orders: dropTable(
        keyspaceName:"papertrader"
        tableName:"orders"
    )
}

mutation createTables {
	users: createTable(
	  keyspaceName:"papertrader"
	  tableName:"users"
	  partitionKeys: [ 
		{ name: "id", type: {basic: TEXT} }
        { name: "username", type: {basic: TEXT }}
	  ],
      values: [
          { name: "name", type: {basic: TEXT }}
          { name: "email", type: {basic: TEXT }}
          { name: "created", type: {basic :DATE }}
      ]
	)
	games: createTable(
		keyspaceName:"papertrader",
		tableName:"games",
		partitionKeys: [ 
		  { name: "id", type: {basic: TEXT} }
		],
        values: [
            { name: "cap", type: {basic: DECIMAL }},
            { name: "created", type: {basic: DATE }},
            { name: "created_by", type : {basic: TEXT }},
            { name: "name", type : {basic: TEXT }},
            { name: "end", type: {basic: DATE }}
        ]
	  )
    portfolios: createTable (
        keyspaceName:"papertrader",
        tableName:"portfolios",
        partitionKeys: [
            {name: "id", type:{basic: TEXT}},
        ],
        clusteringKeys: [
            {name: "game_id", type: {basic: TEXT }},
            {name: "user_id", type: {basic: TEXT }}
        ],
        values: [
            {name: "cash", type: {basic: DECIMAL}},
            {name: "spent", type: {basic: DECIMAL }},
            {name: "value", type: { basic: DECIMAL }}
        ]
    )
    orders: createTable (
        keyspaceName: "papertrader",
        tableName: "orders",
        partitionKeys: [
            {name: "id", type:{basic: TEXT}}
        ],
        clusteringKeys: [
            {name: "user_id", type: {basic: TEXT }},
            {name: "portfolio_id", type: {basic: TEXT }},
        ],
        values: [
            {name: "symbol", type: { basic: TEXT }},
            {name: "action", type: {basic : INT }},
            {name: "created", type: {basic: DATE }},
            {name: "closed", type: {basic: DATE }},
            {name: "type", type: {basic: INT }},
            {name: "ask", type: {basic: DECIMAL }}
            {name: "actual", type:{ basic: DECIMAL }}
        ]
    )
  }
